SELECT count(*) FROM TBL_ORDERS
WHERE  DATA_STATUS = 1

	and ORDER_NO = #{orderNo}


	and SELLER_ID = #{sellerId}


	and BUYER_ID = #{buyerId}

 and ORDER_STATUS = #{orderStatus}

mapper.update:
UPDATE TBL_ORDERS ORDERS
SET ORDERS.ORDER_STATUS = #{orderStatus}

WHERE ORDERS.ORDER_NO =
#{orderNo} AND DATA_STATUS = 1


	AND ORDERS.ORDER_STATUS = #{sourceOrderStatus}

mapper.select:
SELECT o.order_status, COUNT(o.ORDER_STATUS) AS amount FROM TBL_ORDERS o WHERE 1 = 1
and o.seller_Id = #{sellerId}
GROUP BY o.ORDER_STATUS

SELECT TODAYORDERS.TODAY_ORDER, TOMORROWORDERS.TOMORROW_ORDER, DISHES.DISHES, TURNOVER.TURNOVER FROM ( SELECT COUNT(O.ORDER_NO) AS TODAY_ORDER FROM TBL_ORDERS O WHERE O.ORDER_TIME >= CURDATE()
AND O.ORDER_TIME < date_add(CURDATE(), interval 1 day)
and O.SELLER_ID = #{sellerId}
) TODAYORDERS JOIN ( SELECT count(O.ORDER_NO) AS TOMORROW_ORDER FROM TBL_ORDERS O WHERE O.ORDER_TIME >= date_add(CURDATE(), interval 1 day)
AND O.ORDER_TIME < date_add(CURDATE(), interval 2 day)
and O.SELLER_ID = #{sellerId}
) TOMORROWORDERS
JOIN (
SELECT COUNT(D.DISH_ID) AS DISHES FROM TBL_ORDERS O JOIN TBL_REL_ODR_DISH D ON O.ORDER_NO = D.ORDER_NO WHERE
O.ORDER_TIME >= CURDATE()
AND O.ORDER_TIME < date_add(CURDATE(), interval 1 day)
and O.SELLER_ID = #{sellerId}
AND O.ORDER_STATUS = 2
) DISHES
JOIN ( SELECT SUM(O.ORIGINAL_PRICE) TURNOVER FROM TBL_ORDERS O WHERE O.ORDER_TIME = CURRENT_DATE
and O.SELLER_ID = #{sellerId}
) TURNOVER

SELECT REFUND.REFUND, RECEIVED.RECEIVED FROM ( SELECT
COUNT(O.ORIGINAL_PRICE) REFUND
FROM TBL_ORDERS O WHERE O.ORDER_STATUS = 201 OR O.ORDER_STATUS = 202 OR O.ORDER_STATUS = 301 OR O.ORDER_STATUS = 302
and O.SELLER_ID = #{sellerId}
) REFUND JOIN ( SELECT
COUNT(O.ORIGINAL_PRICE) RECEIVED
FROM TBL_ORDERS O WHERE O.ORDER_STATUS = 100
and O.SELLER_ID = #{sellerId}
)RECEIVED
